query:
SELECT ?area 
	WHERE { 
	?uri a <http://dbpedia.org/ontology/BodyOfWater>; <http://dbpedia.org/ontology/areaOfCatchment> ?rel.  ?uri <http://dbpedia.org/ontology/location> ?area } ORDER BY DESC(?rel) LIMIT 1

SELECT ?area 
	WHERE { 
	?uri a <http://dbpedia.org/ontology/Crater>; <http://dbpedia.org/ontology/diameter> ?rel.  ?uri <http://dbpedia.org/ontology/location> ?area } ORDER BY DESC(?rel) LIMIT 1

SELECT ?area 
	WHERE { 
	?uri a <http://dbpedia.org/ontology/Volcano>; <http://dbpedia.org/ontology/elevation> ?rel.  ?uri <http://dbpedia.org/ontology/locatedInArea> ?area } ORDER BY DESC(?rel) LIMIT 1

SELECT ?area 
	WHERE { 
	?uri a <http://dbpedia.org/ontology/MountainRange>; <http://dbpedia.org/ontology/length> ?rel.  ?uri <http://dbpedia.org/ontology/country> ?area } ORDER BY DESC(?rel) LIMIT 1

SELECT ?area 
	WHERE { 
	?uri a <http://dbpedia.org/ontology/MountainRange>; <http://dbpedia.org/ontology/length> ?rel.  ?uri <http://dbpedia.org/ontology/state> ?area } ORDER BY DESC(?rel) LIMIT 1
